class GlozinCounterWidgetHandler extends elementorModules.frontend.handlers.Base{getDefaultSettings(){return{selectors:{counterNumber:".glozin-counter__number"}}}getDefaultElements(){const e=this.getSettings("selectors");return{$counterNumber:this.$element.find(e.counterNumber)}}onInit(){super.onInit();this.intersectionObserver=elementorModules.utils.Scroll.scrollObserver({callback:e=>{if(e.isInViewport){this.intersectionObserver.unobserve(this.elements.$counterNumber[0]);const t=this.elements.$counterNumber.data(),n=t.toValue.toString().match(/\.(.*)/);if(n){t.rounding=n[1].length}this.elements.$counterNumber.numerator(t)}}});this.intersectionObserver.observe(this.elements.$counterNumber[0])}}jQuery(window).on("elementor/frontend/init",()=>{elementorFrontend.hooks.addAction("frontend/element_ready/glozin-counter.default",e=>{elementorFrontend.elementsHandler.addHandler(GlozinCounterWidgetHandler,{$element:e})})});